CREATE TABLE Country_de
(
  Id INTEGER NOT NULL,
  Name VARCHAR(50) NOT NULL,
  PRIMARY KEY(Id)
);

CREATE TABLE Country_ja
(
  Id INTEGER NOT NULL,
  Name VARCHAR(50) NOT NULL,
  PRIMARY KEY(Id)
);

CREATE PROCEDURE GetNewId RETURNS (rowId INTEGER) AS
BEGIN
  SELECT MAX(RowId) + 1 FROM Country INTO :rowId;
END;

